package com.example.multigraph;

import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.os.Bundle;
import android.util.Base64;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Button;
import android.widget.ImageView;
import androidx.annotation.NonNull;
import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.GridLayoutManager;
import androidx.recyclerview.widget.RecyclerView;
import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

public class MainActivity extends AppCompatActivity {

    private String TAG = "ww";
    private ImageView imageView;
    private Button button;
    private RecyclerView recyclerView;
    private Map<String, String> imageMap;
    private List<Bitmap> bitmapList = new ArrayList<>();

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        recyclerView = findViewById(R.id.imageRecyclerView);

        imageView = findViewById(R.id.imageView);

        button = findViewById(R.id.button);
        button.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                displayImages();
            }
        });

        imageMap = new HashMap<>();
        imageMap.put("top_1_image", "");
        imageMap.put("top_2_image", "");
        imageMap.put("top_3_image", "");
        imageMap.put("top_4_image", "");
        imageMap.put("top_5_image", "");
        imageMap.put("top_6_image", "");
        imageMap.put("top_7_image", "");
        imageMap.put("top_8_image", "");
        imageMap.put("top_9_image", "");
        imageMap.put("top_10_image", "");
        imageMap.put("top_11_image", "");

        convertBase64ImagesToBitmaps();

        imageView.setImageResource(R.drawable.test_search_k);

//        String imageStr = imageMap.get("top_1_image");
//        byte[] decodedBytes = Base64.decode(imageStr, Base64.DEFAULT);
//        Bitmap bitmap = BitmapFactory.decodeByteArray(decodedBytes, 0, decodedBytes.length);
//        imageView.setImageBitmap(bitmap);
    }

    private void displayImages() {
        ImageAdapter imageAdapter = new ImageAdapter();
        recyclerView.setLayoutManager(new GridLayoutManager(this, 2));
        recyclerView.setAdapter(imageAdapter);
    }

    private void convertBase64ImagesToBitmaps() {
        for (String imageData : imageMap.values()) {
            byte[] decodedString = Base64.decode(imageData, Base64.DEFAULT);
            Bitmap bitmap = BitmapFactory.decodeByteArray(decodedString, 0, decodedString.length);
            bitmapList.add(bitmap);
        }
    }

    private class ImageAdapter extends RecyclerView.Adapter<ImageAdapter.ImageViewHolder> {

        @NonNull
        @Override
        public ImageViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
            View view = LayoutInflater.from(parent.getContext()).inflate(R.layout.item_image, parent, false);
            return new ImageViewHolder(view);
        }

        @Override
        public void onBindViewHolder(@NonNull ImageViewHolder holder, int position) {
            Bitmap bitmap = bitmapList.get(position);
            holder.imageView.setImageBitmap(bitmap);
        }

        @Override
        public int getItemCount() {
            return bitmapList.size();
        }

        private class ImageViewHolder extends RecyclerView.ViewHolder {
            ImageView imageView;

            ImageViewHolder(@NonNull View itemView) {
                super(itemView);
                imageView = itemView.findViewById(R.id.imageView);
            }
        }
    }

}
